<template>    <div class="dy-checklist-wrap">        <label v-for="item in currentOptions" class="dy-checkbox-label">            <input type="checkbox" v-model="currentValue"  :value="item.value" class="dy-checkbox-input">            <span class="dy-checkbox-border">                <i class="dy-checkbox-active"></i>            </span>            <span class="dy-checkbox-title">                {{item.label}}            </span>        </label>    </div></template><script>    export default {        name: "checkList",        props: {            options: {                type: Array,                require: true            },            value: {                type: Array            }        },        data() {            return {                currentValue: [],                currentOptions: []            }        },        created() {            this.currentValue = this.value;            this.currentOptions = this.options;        },        watch: {            value(val) {                this.currentValue = val;            },            currentValue(val) {                this.$emit('input',val);            },            options(val){                this.currentOptions = val;            }        }    }</script><style>    .dy-checkbox-label {        display: inline-block;        margin-right: 30px;        color: #999;        font-size: 14px;        vertical-align: middle;        cursor: pointer;        user-select: none;    }    .dy-checkbox-input {        display: none;    }    .dy-checkbox-border {        position: relative;        display: inline-block;        width: 18px;        height: 18px;        border: 1px solid #dcdfe6;        border-radius: 2px;        margin-right: 10px;        color: #fff;        vertical-align: middle;    }    .dy-checkbox-active {        position: absolute;        display: block;        width: 7px;        height: 4px;        left: 4px;        top: 4px;        transform: rotate(-45deg) scaleY(0) scaleX(0);        border-left: 3px solid #fff;        border-bottom: 3px solid #fff;        transition: transform .2s ease;    }    .dy-checkbox-input:checked ~ .dy-checkbox-border {        background: #c30d23;    }    .dy-checkbox-input:checked ~ .dy-checkbox-border>.dy-checkbox-active {        transform: rotate(-45deg) scaleY(1) scaleX(1);    }</style>