<template>    <div class="select-city" @click="isShowMark = true">        <span>{{selectText}}        <i class="iconfont icon-jiantouxia"></i></span>        <div class="mark" v-show="isShowMark" @click.stop="isShowMark = false">            <div class="select-box">                <div class="box-title">                    <span>选择区县</span>                    <i class="iconfont icon-chahao" @click="isShowMark = false"></i>                </div>                <div class="select-content clearfix">                    <div class="current-item">                        <span @click="handleChangeArea('呼和浩特')">呼和浩特</span>                    </div>                    <ul>                        <li v-for="item in regionList">                            <span class="item-char fll">                                {{item.key}}                            </span>                            <span class="item-text" @click="handleChangeArea(item.list[0].area)">                                {{item.list[0].area}}                            </span>                        </li>                    </ul>                </div>            </div>        </div>    </div></template><script>    import api from '~/api'    export default {        data() {            return {                selectText: "呼和浩特",                isShowMark: false,                regionList: []            }        },        methods: {            handleChangeArea(area) {                localStorage.setItem("area", area);                this.selectText = area;                this.isShowMark = false;            },            getData() {                this.$axios.get(api.regionListCopy).then(res => {                    this.regionList = res.data.data;                })            },            initArea() {                let area = localStorage.getItem("area");                if(!area){                    this.selectText = "呼和浩特";                }                else {                    this.selectText = area;                }            }        },        mounted() {           this.getData();           this.initArea();        }    }</script><style scoped lang="scss">.select-city {    user-select: none;    display: table-cell;    vertical-align: middle;    width: 210px;    cursor: pointer;    &>span {        margin-left: 20px;        border: 1px solid #999;        padding: 4px 10px;        border-radius: 20px;        color: #333333    }}.mark {    position: fixed;    z-index: 1000;    top: 0;    left: 0;    width: 100%;    height: 100%;    background: rgba(0,0,0,.3);}.select-box {    width: 640px;    background: #fff;    z-index: 1002;    position: fixed;    left: 50%;    top: 50%;    margin-left: -320px;    margin-top: -160px;    padding: 40px;    text-align: left;}.box-title {    border-bottom: 1px solid #ccc;    padding-bottom: 10px;    span {        display: block;        font-size: 22px;        color: #333;        font-weight: bold;        text-align: left;    }    i {        cursor: pointer;        width: 30px;        height: 30px;        /* color: #fff; */        border: 1px solid #fff;        background: white;        border-radius: 50%;        text-align: center;        line-height: 30px;        position: absolute;        top: 5px;        right: 5px;    }}.current-item {    margin: 20px 0;    color: #333;    font-size: 18px;    cursor: pointer;}.select-content li {    margin-top: 20px;    font-size: 16px;}.item-char {    color: #999;    margin-right: 8px;}.item-text {    cursor: pointer;    user-select: none;    font-weight: 400;    color: #333;}.select-content li:hover .item-text {    color: #c30d23;}</style>