<template>    <div class="content">        <div class="base-nav clearfix" v-if="houseDetails">            <breadcrumb class="fll">{{houseDetails.jibenxinxi.village}}</breadcrumb>            <collect class="flr" :collect="houseDetails.jibenxinxi.shoucang == 1" ></collect>        </div>        <h1 class="house-title" v-if="houseDetails">{{houseDetails.jibenxinxi.title}}</h1>        <!--顶部信息展示区域-->        <base-msg v-if="houseDetails"></base-msg>        <!--顶部信息展示区域-->        <!--基本信息-->        <baseInformation style="margin-top: 80px;"></baseInformation>        <!--基本信息结束-->        <!--房源特色-->        <houseFeature style="margin-top: 80px;"></houseFeature>        <!--房源特色-->        <!--房源照片-->        <housePhoto></housePhoto>        <!--房源照片-->        <!--周边配套-->        <mapTab style="margin-top: 80px;" v-if="houseDetails" :lat="houseDetails.jibenxinxi.lat" :log="houseDetails.jibenxinxi.log"></mapTab>    </div></template><script>    import breadcrumb from '~/components/secondHand/breadcrumb'    import collect from '~/components/secondHand/collect'    import baseMsg from '~/components/secondHand/baseMsg'    import mapTab from '~/components/secondHand/mapTab'    import baseInformation from '~/components/secondHand/baseInformation'    import houseFeature from '~/components/secondHand/houseFeature'    import housePhoto from '~/components/secondHand/housePhoto'    import api from '~/api'    import axios from '~/plugins/axios'    import {mapMutations, mapState} from 'vuex'    export default {        layout: "secondHandDetails",        components: {            breadcrumb,            collect,            baseMsg,            mapTab,            baseInformation,            houseFeature,            housePhoto        },        // async fetch(context){        //     let houseDetails = await axios.get(api.paramToUrl(api.used_detail, {id: context.route.params.id}))        //     context.store.commit("setHouseDetails",houseDetails.data.data)        // },        data() {            return {}        },        computed:{            ...mapState(["houseDetails","userid"])        },        methods: {            getData() {                axios.get(api.paramToUrl(api.used_detail, {id: this.$route.params.id})).then(res => {                    this.setHouseDetails(res.data.data);                })            },            async getHouseData(){                let params = {id: this.$route.params.id}                if(this.userid){                    params.userid = this.userid                }                let houseDetails = await axios.get(api.paramToUrl(api.used_detail, params))                this.$store.commit("setHouseDetails",houseDetails.data.data)            },            ...mapMutations(["setHouseDetails"])        },        created() {            this.getHouseData();        },    }</script><style scoped>    .content {        width: 1100px;        margin: 20px auto 0;    }    .house-title {        margin-top: 36px;        font-size: 30px;        color: #333;        cursor: pointer;    }</style>